    private final VictorSP motorRamasseurAvant = RobotMap.ramasseurMoteurAvant; //Gauche
    private final DoubleSolenoid pistonPushDroit = RobotMap.pistonPushDroit;
    private final DoubleSolenoid pistonPushGauche = RobotMap.pistonPushGauche;
    private final DoubleSolenoid pistonLiftDroit = RobotMap.pistonLiftDroit;
    private final DoubleSolenoid pistonLiftGauche = RobotMap.pistonLiftGauche;
