    public void Stop(){
    	motorRamasseurArriere.set(0);
    	motorRamasseurAvant.set(0);
    	
  
    }
    public void Ramasser(){
    	motorRamasseurArriere.set(-0.5);
    	motorRamasseurAvant.set(-0.5);
    
    }  
    
    public void Eject(){
    	motorRamasseurArriere.set(0.5);
    	motorRamasseurAvant.set(0.5);
    
    
    
    
   }
    public void Extract(){
    	pistonPushDroit.set(DoubleSolenoid.Value.kForward);  //probablement changer dans false
    	pistonPushGauche.set(DoubleSolenoid.Value.kForward);
   }
    public void Retract(){
    	pistonPushDroit.set(DoubleSolenoid.Value.kReverse); 
    	pistonPushGauche.set(DoubleSolenoid.Value.kReverse);
   }
    public void Up(){
    	pistonLiftDroit.set(DoubleSolenoid.Value.kForward);
    	pistonLiftGauche.set(DoubleSolenoid.Value.kForward);
   } 	
    public void Down(){
    	pistonLiftDroit.set(DoubleSolenoid.Value.kReverse);
    	pistonLiftGauche.set(DoubleSolenoid.Value.kReverse);
   }
    
   public void disablePneumatic(){
	pistonPushDroit.set(DoubleSolenoid.Value.kOff);  //probablement changer dans false
   	pistonPushGauche.set(DoubleSolenoid.Value.kOff);
   	pistonLiftDroit.set(DoubleSolenoid.Value.kOff);
	pistonLiftGauche.set(DoubleSolenoid.Value.kOff);
	   
   }
